import { Card, Space } from "antd";

function DeviceCard({ deviceDetails }) {
  return (
    <div>
      <Card
        headStyle={{
          color: "white",
          background: "#3E3D40",
        }}
        title={
          <div>
            <b>Hostname: </b>
            {deviceDetails.name}
          </div>
        }
        style={{
          width: "100%",
          overflow: "hidden",
          color: "white",
          margin: "0 auto",
        }}
        bodyStyle={{ background: "#1F1E26" }}
      >
        <Space size={21}>
          <p>Device Status:</p>{" "}
          <p style={{ textTransform: "uppercase", color: "#E14942" }}>
            {deviceDetails.status === "Active" ? (
              <p style={{ color: "#45d5c9" }}>
                <b>Active</b>
              </p>
            ) : deviceDetails.status === "Inactive" ? (
              <p style={{ color: "#F9D14D" }}>
                <b>Inactive</b>
              </p>
            ) : (
              <b>Inactive</b>
            )}
          </p>
        </Space>
        <Space size={25}>
          <p>Device Type:</p> <p>{deviceDetails.type}</p>
        </Space>
        <Space size={28}>
          <p>OS Version:</p> <p>{deviceDetails.os}</p>
        </Space>
        <Space size={23}>
          <p>Mac Address:</p> <p>{deviceDetails.mac_addr}</p>
        </Space>
      </Card>
    </div>
  );
}

export default DeviceCard;
