import React, { useState } from "react";
import "antd/dist/antd.css";
import { Drawer, Button, Col, Row } from "antd";
import TestCard from "./Card";
import EmergingThreatsCard from "./CardEmergingThreats";
import StatisticsBoxes from "./Statistics";
import ThreatsTable from "./TableThreats";

function NetworkDrawer({
  networkDetails,
  networkDrawerStatus,
  closeNetworkDrawerFunction,
  deviceCounter,
}) {
  return (
    <div>
      <Drawer
        title={
          <div>
            Home / <b>Network Statistics{deviceCounter}</b>
          </div>
        }
        style={{ color: "white" }}
        maskStyle={{
          color: "white",
        }}
        headerStyle={{
          // backgroundColor: "#16151B",
          backgroundColor: "white",
          borderBottomWidth: 0,
        }}
        bodyStyle={{
          backgroundColor: "#16151B",
          color: "white",
        }}
        width={"650px"}
        placement="right"
        closable={true}
        mask={false}
        keyboard={true}
        visible={networkDrawerStatus}
        onClose={closeNetworkDrawerFunction}
      >
        <div>
          <Row gutter={25}>
            <Col span={12}>
              <TestCard networkDetails={networkDetails} />
            </Col>
            <Col span={12}>
              <EmergingThreatsCard />
            </Col>
          </Row>
        </div>
        <br />
        <StatisticsBoxes />
        <br />
        <ThreatsTable />
      </Drawer>
    </div>
  );
}

export default NetworkDrawer;

