import { Statistic, Card, Row, Col } from "antd";
import "../CustomCSS.css";
function StatisticsBoxes({ deviceCountData }) {
  const groupByDeviceType = () => {
    var result = {};
    for (var i = 0; i < deviceCountData.length; i++) {
      if (!result[deviceCountData[i]["status"]]) {
        result[deviceCountData[i]["status"]] = 0;
      }
      result[deviceCountData[i]["status"]]++;
    }
    return result;
  };

  return (
    <div className="site-statistic-demo-card">
      <p>{groupByDeviceType()}</p>
      <Row gutter={25}>
        <Col span={6}>
          <Card bodyStyle={{ background: "#1F1E26" }}>
            <Statistic
              title="Total Devices"
              value={deviceCountData.length}
              valueStyle={{
                color: "white",
                fontWeight: "bolder",
                fontSize: "18px",
              }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card bodyStyle={{ background: "#1F1E26" }}>
            <Statistic
              title="Active Devices"
              value={0}
              valueStyle={{
                color: "#45D5C9",
                fontWeight: "bolder",
                fontSize: "18px",
              }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card bodyStyle={{ background: "#1F1E26" }}>
            <Statistic
              title="Inactive Devices"
              value={0}
              valueStyle={{
                color: "#FFC130",
                fontWeight: "bolder",
                fontSize: "18px",
              }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card bodyStyle={{ background: "#1F1E26" }}>
            <Statistic
              title="Offline Devices"
              value={0}
              valueStyle={{
                color: "#FA816E",
                fontWeight: "bolder",
                fontSize: "18px",
              }}
            />
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default StatisticsBoxes;
