import { ConsoleSqlOutlined } from "@ant-design/icons";
import { Card, Space, Tooltip } from "antd";

import { Item } from "react";

function DeviceCard({ deviceDetails, deviceConnected }) {
  console.log("123:", deviceConnected[0]);

  return (
    <div>
      <Card
        headStyle={{
          color: "white",
          background: "#3E3D40",
        }}
        title={<div>Device Details</div>}
        style={{
          width: "100%",
          overflow: "hidden",
          color: "white",
          margin: "0 auto",
        }}
        bodyStyle={{ background: "#1F1E26" }}
      >
        <Space size={28}>
          <p>Device Name</p>{" "}
          <p>
            <b>{deviceDetails.name}</b>
          </p>
        </Space>
        <Space size={32}>
          <p>Device Type</p>{" "}
          <p>
            <b>{deviceDetails.type}</b>
          </p>
        </Space>
        <Space size={35}>
          <p>OS Version</p>{" "}
          <p>
            <b>{deviceDetails.os}</b>
          </p>
        </Space>
        <Space size={29}>
          <p>Mac Address</p>{" "}
          <p>
            <b>{deviceDetails.mac_addr}</b>
          </p>
        </Space>
        <Space size={9}>
          <p style={{ whiteSpace: "nowrap" }}>Connected Device(s)</p>{" "}
          <p>
            <Tooltip placement="bottom" color="white" key="red" title={"123"}>
              <p>
                <b>{deviceConnected.length + " device(s)"}</b>
              </p>
            </Tooltip>
          </p>
        </Space>
      </Card>
      <Card
        headStyle={{
          color: deviceDetails.status !== "Offline" ? "#16151b" : "white",
          textAlign: "center",
          background:
            deviceDetails.status === "Active"
              ? "#63D196"
              : deviceDetails.status === "Inactive"
              ? "#F9D14D"
              : "#F1544D",
        }}
        title={
          <div>
            {deviceDetails.status === "Active" ? (
              <b>CONNECTED DEVICE</b>
            ) : deviceDetails.status === "Inactive" ? (
              <b>INACTIVE DEVICE</b>
            ) : (
              <b>DEVICE IS OFFLINE</b>
            )}
          </div>
        }
        style={{
          width: "100%",
          overflow: "hidden",
          color: "white",
          margin: "0 auto",
        }}
        bodyStyle={{ background: "#16151b" }}
      ></Card>
    </div>
  );
}

export default DeviceCard;
