function AppNetworkTopology() {
  const [deviceDrawerStatus, setDeviceDrawerStatus] = useState(false);
  const [networkDrawerStatus, setNetworkDrawerStatus] = useState(false); // networkDrawerStatus={this.state.networkDrawerStatus}
  const [deviceDetails, setDeviceDetails] = useState({});
  const [networkDetails, setNetworkDetails] = useState({}); // networkDetailObject={this.state.networkDetailObject}

  // networkStatisticsObject={this.state.networkStatisticsObject}
  // networkDeviceCounter={this.state.networkDeviceCounter}
  // closeNetworkDrawerFunction={this.closeNetworkDrawer}

  const closeDeviceDrawer = () => {
    setDeviceDrawerStatus(false);
  };

  const handleClick = (node) => {
    setDeviceDrawerStatus(true);
    setDeviceDetails(node);
  };

  const handleNetworkStatisticsButton = () => {
    setDeviceDrawerStatus(false);

    // this.setState({
    //   networkDrawerStatus: true,
    //   deviceDrawerStatus: false,
    // });
  };

  const groupByDeviceType = device_data.reduce((thisData, dev) => {
    thisData[dev.status] = thisData[dev.status] + 1 || 1;
    return thisData;
  }, {});

  return (
    <div>
      <Layout style={{ marginLeft: 199 }}>
        {" "}
        <ForceGraph3D
          style={{ zIndex: -1 }}
          width={window.innerWidth - 200}
          graphData={{
            nodes: device_data,
            links: relation_data,
          }}
          onNodeRightClick={handleClick}
          // linkDirectionalParticles={1}
          // linkDirectionalParticleSpeed={0.01}
          // onNodeHover={props.handleHover}
          onNodeDragEnd={(node) => {
            node.fx = node.x;
            node.fy = node.y;
            node.fz = node.z;
          }}
          nodeThreeObject={({ type }) => {
            let imgTexture = null;
            if (type === "DB") {
              imgTexture = new THREE.TextureLoader().load("assets/img_db.png");
            } else if (type === "PC") {
              imgTexture = new THREE.TextureLoader().load("assets/img_pc.png");
            } else if (type === "Router") {
              imgTexture = new THREE.TextureLoader().load(
                "assets/img_router.png"
              );
            }

            const material = new THREE.SpriteMaterial({ map: imgTexture });
            const sprite = new THREE.Sprite(material);
            sprite.scale.set(12, 12);

            return sprite;
          }}
        />
      </Layout>
      <Affix style={{ position: "absolute", top: 120, left: 10 }}>
        <Button type="primary" onClick={handleNetworkStatisticsButton}>
          Show Network Statistics
        </Button>
      </Affix>

      <DeviceDrawer
        deviceDrawerStatus={deviceDrawerStatus}
        deviceDetails={deviceDetails}
        closeDeviceDrawer={closeDeviceDrawer}
      />
      <NetworkDrawer

      // networkDetailObject={this.state.networkDetailObject}
      // networkDrawerStatus={this.state.networkDrawerStatus}
      // networkStatisticsObject={this.state.networkStatisticsObject}
      // networkDeviceCounter={this.state.networkDeviceCounter}
      // closeNetworkDrawerFunction={this.closeNetworkDrawer}
      />
    </div>
  );
}

export default AppNetworkTopology;
