import React, { useState } from "react";
import "antd/dist/antd.css";
import { Drawer, Button } from "antd";

export default function App() {
  const [visible, setVisible] = useState(false);

  return (
    <div
      style={{
        display: "block",
        width: 1000,
        padding: 30,
      }}
    >
      <h4>ReactJS Ant-Design Drawer Component</h4>
      <>
        <Button
          type="primary"
          onClick={() => {
            setVisible(true);
          }}
        >
          Open
        </Button>
        <Drawer
          title="Drawer Demo"
          placement="right"
          closable={false}
          visible={visible}
          onClose={() => {
            setVisible(false);
          }}
        ></Drawer>
      </>
    </div>
  );
}
