import "antd/dist/antd.css";
import React, { useState } from "react";
import { ForceGraph3D } from "react-force-graph";
import DeviceDrawer from "./Components/DrawerDevice";
import * as THREE from "three";
import testingData from "./Components/Data/testdata.json";

class AppNetworkTopology extends React.Component {
  componentDidMount() {
    this.setState({});
  }

  handleClick = (node) => {
    const getHostName = testingData.filter((thisDevice) =>
      thisDevice.id.includes(node.id)
    );
    alert(getHostName);
  };

  render() {
    return (
      <div>
        <DeviceDrawer />
        <ForceGraph3D
          graphData={{
            nodes: [
              {
                id: "192.168.1.100",
                name: "DB-01",
                type: "DB",
                value: "1",
                os: "Linux",
                mac_addr: "aa:aa:aa:aa:aa:aa",
              },
              {
                id: "192.168.1.101",
                name: "PC-01",
                type: "PC",
                value: "1",
                mac_addr: "bb:bb:bb:bb:bb:bb",
              },
              {
                id: "192.168.1.102",
                name: "PC-02",
                type: "PC",
                value: "1",
                mac_addr: "cc:cc:cc:cc:cc:cc",
              },
            ],
            links: [
              {
                source: "192.168.1.100",
                target: "192.168.1.101",
              },
              {
                source: "192.168.1.101",
                target: "192.168.1.102",
              },
              {
                source: "192.168.1.100",
                target: "192.168.1.102",
              },
            ],
          }}
          onNodeRightClick={this.handleClick}
          linkDirectionalParticles={1}
          linkDirectionalParticleSpeed={0.01}
          onNodeDragEnd={(node) => {
            node.fx = node.x;
            node.fy = node.y;
            node.fz = node.z;
          }}
          nodeThreeObject={({ type }) => {
            let imgTexture = null;
            if (type === "DB") {
              imgTexture = new THREE.TextureLoader().load("assets/img_db.png");
            } else if (type === "PC") {
              imgTexture = new THREE.TextureLoader().load("assets/img_pc.png");
            }

            const material = new THREE.SpriteMaterial({ map: imgTexture });
            const sprite = new THREE.Sprite(material);
            sprite.scale.set(12, 12);

            return sprite;
          }}
        />
      </div>
    );
  }
}

export default AppNetworkTopology;

