import React, { useState } from "react";
import "antd/dist/antd.css";
import "./index.css";
import { Table, Space } from "antd";
import ManageDrawer from "./Components/DrawerManage";
import device_data from "./Components/Data/device_data.json";

const { Column } = Table;

function AppManageDevice() {
  const [manageDrawerStatus, setManageDrawerStatus] = useState(false);
  const [manageDeviceDetails, setManageDeviceDetails] = useState({});

  // When user clicks on "Edit", two functions will run:
  // - openManageDrawerStatus - To open device drawer
  // -
  function onEditClick(record) {
    openManageDrawerStatus();
    passManageDeviceDetails(record);
  }

  const openManageDrawerStatus = () => {
    setManageDrawerStatus(true);
  };

  const passManageDeviceDetails = (record) => {
    setManageDeviceDetails(record);
  };

  return (
    <div>
      <ManageDrawer
        manageDrawerStatus={manageDrawerStatus}
        manageDeviceDetails={manageDeviceDetails}
      />
      <Table dataSource={device_data}>
        <Column
          title="Action"
          key="action"
          render={(text, record) => (
            <Space size="middle">
              <a onClick={() => onEditClick(record)}>Edit</a>
              <a>Remove</a>
            </Space>
          )}
        />
        <Column title="IP Address" dataIndex="id" key="id" />
        <Column title="Hostname" dataIndex="name" key="name" />
        <Column title="Device Type" dataIndex="type" key="type" />
        <Column title="OS Version" dataIndex="os" key="os" />
        <Column title="Mac Address" dataIndex="mac_addr" key="mac_addr" />
        <Column title="Ownership" dataIndex="ownership" key="ownership" />
      </Table>
    </div>
  );
}

export default AppManageDevice;
