import React, { useState } from "react";
import "antd/dist/antd.css";
import { Drawer, Button, Col, Row } from "antd";
import TestCard from "./Card";
import EmergingThreatsCard from "./CardEmergingThreats";
import StatisticsBoxes from "./Statistics";
import ThreatsTable from "./TableThreats";

function NetworkDrawer() {
  const [visible, setVisible] = useState(false);

  return (
    <div
      style={{
        display: "block",
        width: 700,
        padding: 30,
      }}
    >
      <h4>Test Drawer</h4>
      <Button
        type="primary"
        onClick={() => {
          setVisible(true);
        }}
      >
        Open Drawer
      </Button>
      <br />
      <br />
      <Button
        type="primary"
        onClick={() => {
          setVisible(false);
        }}
      >
        Close Drawer
      </Button>
      <Drawer
        title={
          <div>
            Home / <b>Network Statistics</b>
          </div>
        }
        style={{ color: "white" }}
        maskStyle={{
          color: "white",
        }}
        headerStyle={{
          // backgroundColor: "#16151B",
          backgroundColor: "white",
          borderBottomWidth: 0,
        }}
        bodyStyle={{
          backgroundColor: "#16151B",
          color: "white",
        }}
        width={"650px"}
        placement="right"
        closable={true}
        mask={false}
        keyboard={true}
        visible={visible}
        onClose={() => {
          setVisible(false);
        }}
      >
        <div>
          <Row gutter={25}>
            <Col span={12}>
              <TestCard />
            </Col>
            <Col span={12}>
              <EmergingThreatsCard />
            </Col>
          </Row>
        </div>
        <br />
        <StatisticsBoxes />
        <br />
        <ThreatsTable />
      </Drawer>
    </div>
  );
}

export default NetworkDrawer;
