import "antd/dist/antd.css";
import React, { useState } from "react";
import { ForceGraph3D } from "react-force-graph";
import DeviceDrawer from "./Components/DrawerDevice";
import * as THREE from "three";
import testingData from "./Components/Data/testdata.json";

class App extends React.Component {
  state = {
    mssg: "",
  };

  handleClick = () => {
    this.setState({ mssg: "Hi there!" });
  };

  render() {
    console.log("render() method");
    return (
      <>
        <button onClick={this.handleClick}>Say something</button>
        <div>{this.state.mssg}</div>
      </>
    );
  }
}

export default AppNetworkTopology;

