import React, { useState, memo } from "react";
import "antd/dist/antd.css";
import "./index.css";
import { Form, Layout, Table, Space, Popconfirm } from "antd";
import ManageDrawer from "./Components/DrawerManage";
import device_data from "./Components/Data/device_data.json";
import { render } from "@testing-library/react";

const { Column } = Table;

const columns = [
  {
    title: "Action",
    key: "action",
    width: 150,
    render: (record) => (
      <Space size="middle">
        <a onClick={() => onEditClick(record)}>Edit</a>
        <Popconfirm
          title="Are you sure?"
          okText="Yes"
          cancelText="No"
          placement="bottom"
          onConfirm={() => tester_delete(record)}
        >
          <a>Remove</a>
        </Popconfirm>
      </Space>
    ),
  },
  {
    title: "IP Address",
    dataIndex: "id",
    key: "id",
    width: 150,
  },
  {
    title: "Hostname",
    dataIndex: "name",
    key: "name",
    width: 150,
  },
  {
    title: "Device Type",
    dataIndex: "type",
    key: "type",
    width: 150,
  },
  {
    title: "OS Version",
    dataIndex: "os",
    key: "os",
    width: 150,
  },
  {
    title: "Mac Address",
    dataIndex: "mac_addr",
    key: "mac_addr",
    width: 150,
  },
  {
    title: "Ownership",
    dataIndex: "ownership",
    key: "ownership",
    width: 150,
  },
];

function AppManageDevice() {
  const [manageDrawerStatus, setManageDrawerStatus] = useState(false);
  const [manageDeviceDetails, setManageDeviceDetails] = useState({});
  const [deviceTableData, updateDeviceTableData] = useState(device_data);

  const [deviceForm] = Form.useForm();

  // When user clicks on "Edit", two functions will run:
  // - openManageDrawerStatus - To open device drawer
  // - setManageDeviceDetails - Pass the record to FormDeviceMange page (for the Revert button)
  // - setFormValue - Directly update the values in the Form component
  async function onEditClick(record) {
    openManageDrawerStatus();
    setManageDeviceDetails(record);
    setFormValues(record);
  }

  const openManageDrawerStatus = () => {
    setManageDrawerStatus(true);
  };

  const closeMangeDeviceDrawer = () => {
    setManageDrawerStatus(false);
  };

  const setFormValues = (record) => {
    deviceForm.setFieldsValue({
      hostname: record.name,
      device_type: record.type,
      os_version: record.os,
      mac_address: record.mac_addr,
      ownership: record.ownership,
    });
  };

  // To update the values in the table based on the new
  const updatedFormValues = (updated) => {
    // Sample console log for verification only
    console.log(
      "Updated hostname to",
      updated.hostname,
      "where IP Address is",
      manageDeviceDetails.id
    );

    updateTableValues(updated);
  };

  // Function to update the table with the updated values from the user
  function updateTableValues(updated) {
    for (var i = 0; i < device_data.length; i++) {
      if (device_data[i].id == manageDeviceDetails.id) {
        device_data[i].name = updated.hostname;
        break;
      }
    }
    console.log("Table Updated:", device_data[i].name);
  }

  function tester_delete(recordToDelete) {
    console.log(recordToDelete.id);
    for (var i = 0; i < device_data.length; i++) {
      if (device_data[i].id == recordToDelete.id) {
        device_data.splice(i, 1);
      }
    }
  }

  return (
    <div>
      <ManageDrawer
        manageDrawerStatus={manageDrawerStatus}
        manageDeviceDetails={manageDeviceDetails}
        closeMangeDeviceDrawer={closeMangeDeviceDrawer}
        deviceForm={deviceForm}
        updatedFormValues={updatedFormValues}
      />
      <Layout style={{ marginLeft: 199 }}>
        <Table dataSource={[...device_data]} columns={columns} />
      </Layout>
    </div>
  );
}

export default AppManageDevice;
