import React, { useEffect } from "react";
import "antd/dist/antd.css";
import { Form, Input, Button, Select } from "antd";
const { Option } = Select;

const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};

export default function DeviceManageForm({ manageDeviceDetails }) {
  // When user save changes, run onFinish()
  const onFinish = (values) => {
    // console.log(values);
    localStorage.setItem("newUpdate", JSON.stringify(values));

    let storedData = localStorage.getItem("newUpdate");
    storedData = JSON.parse(storedData);
    console.log(storedData);
  };

  const [form] = Form.useForm();

  return (
    <div>
      <Button type="primary">Change age to 25</Button>
      <Form
        form={form}
        {...layout}
        name="nest-messages"
        onFinish={onFinish}
        initialValues={{
          hostname: manageDeviceDetails.id,
          device_type: manageDeviceDetails.type,
          os_version: manageDeviceDetails.os,
          mac_address: manageDeviceDetails.mac_addr,
          ownership: manageDeviceDetails.ownership,
        }}
      >
        {/* Form Item for device ownership*/}
        <Form.Item
          name="hostname"
          label="Hostname"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Input />
        </Form.Item>

        {/* Form Item for Device Type */}
        <Form.Item
          name={"device_type"}
          label="Device Type"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Select>
            <Option value="PC">PC</Option>
            <Option value="Router">Router</Option>
            <Option value="Server">Server</Option>
          </Select>
        </Form.Item>

        {/* Form Item for OS Version*/}
        <Form.Item
          name="os_version"
          label="OS Version"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Select>
            <Option value="Windows 11">Windows 11</Option>
            <Option value="Windows 10">Windows 10</Option>
            <Option value="Windows 7">Windows 7</Option>
            <Option value="MacOS">MacOS</Option>
            <Option value="Linux">Linux</Option>
            <Option value="Others">Others</Option>
          </Select>
        </Form.Item>

        {/* Form Item for Mac Address*/}
        <Form.Item
          name="mac_address"
          label="Mac Address"
          rules={[
            {
              required: true,
              min: 17,
              max: 17,
            },
          ]}
        >
          <Input />
        </Form.Item>

        {/* Form Item for device ownership*/}
        <Form.Item
          name="ownership"
          label="Device Ownership"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Select>
            <Option value="Corporate">Corporate</Option>
            <Option value="Personal">Personal</Option>
          </Select>
        </Form.Item>

        {/* Form Item for device ownership*/}
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
          <Button type="primary" htmlType="submit">
            Save Changes
          </Button>
          <Button
            htmlType="button"
            style={{
              margin: "0 8px",
            }}
          >
            Revert
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
}
