import { Card, Space } from "antd";

function DeviceCard({ deviceDetails, deviceConnectedCount }) {
  return (
    <div>
      <Card
        headStyle={{
          color: "white",
          background: "#3E3D40",
        }}
        title={
          <div>
            <b>Device Details</b>
          </div>
        }
        style={{
          width: "100%",
          overflow: "hidden",
          color: "white",
          margin: "0 auto",
        }}
        bodyStyle={{ background: "#1F1E26" }}
      >
        <Space size={27}>
          <p>Device Name:</p>{" "}
          <p>
            <b>{deviceDetails.type}</b>
          </p>
        </Space>
        <Space size={31}>
          <p>Device Type:</p>{" "}
          <p>
            <b>{deviceDetails.type}</b>
          </p>
        </Space>
        <Space size={34}>
          <p>OS Version:</p>{" "}
          <p>
            <b>{deviceDetails.os}</b>
          </p>
        </Space>
        <Space size={29}>
          <p>Mac Address:</p>{" "}
          <p>
            <b>{deviceDetails.mac_addr}</b>
          </p>
        </Space>
        <Space size={10}>
          <p style={{ whiteSpace: "nowrap" }}>Connected Device(s):</p>{" "}
          <p>
            <b>{deviceConnectedCount} device(s)</b>
          </p>
        </Space>
      </Card>
      <Card
        headStyle={{
          color: "#16151b",
          textAlign: "center",
          background: "#63D196",
        }}
        title={
          <div>
            <b>ONLINE</b>
          </div>
        }
        style={{
          width: "100%",
          overflow: "hidden",
          color: "white",
          margin: "0 auto",
        }}
        bodyStyle={{ background: "#16151b" }}
      ></Card>
    </div>
  );
}

export default DeviceCard;
