import { Card } from "antd";
import alerts from "./Data/alert.json";

// var counter = alerts.reduce(function (prev, cur) {
//   if (prev.hasOwnProperty(cur.type)) prev[cur.type] += 1;
//   else prev[cur.type] = 1;

//   return prev;
// }, {});

// alert(JSON.stringify(groupByThreatCategory));

// for (const [key, value] of Object.entries(groupByThreatCategory)) {
//   console.log(`${key}: ${value}`);
// }

function EmergingThreatsCard() {
  // Group the alerts by their Threat Category
  const groupByThreatCategory = alerts.reduce((alertData, alertCounter) => {
    alertData[alertCounter.threat_category] =
      alertData[alertCounter.threat_category] + 1 || 1;
    return alertData;
  }, {});

  // Sort the groups (of Threat Category) by highest to lowest frequency (no. of occurances)
  const sortByFrequency = Object.fromEntries(
    Object.entries(groupByThreatCategory)
      .sort(([, a], [, b]) => a - b)
      .reverse()
  );
  console.log(sortByFrequency);

  const topFourThreatCategories = Object.entries(sortByFrequency).splice(0);
  console.log(topFourThreatCategories);

  return (
    <div>
      <Card
        headStyle={{
          color: "white",
          background: "#3E3D40",
        }}
        title="Emerging Threats"
        style={{
          width: "100%",
          overflow: "hidden",
          color: "white",
          margin: "0 auto",
        }}
        bodyStyle={{ background: "#1F1E26" }}
      >
        {Object.entries(topFourThreatCategories).map((entry) => {
          return <p key={entry[0]}>{`${entry[1][0]}: ${entry[1][1]}`}</p>;
        })}
        <ul id="abcd"></ul>
      </Card>
    </div>
  );
}

export default EmergingThreatsCard;

<div>
  {alerts.map((threats, key) => {
    return <p key={key}>{threats.threat_category}</p>;
  })}
</div>;
