import React, { useEffect, useState } from "react";
import "antd/dist/antd.css";
import { Drawer, Button, Col, Row } from "antd";
import DeviceCard from "./Card_Device";
import EmergingThreatsCard from "./CardEmergingThreats";
import StatisticsBoxes from "./Statistics";
import ThreatsTable from "./TableThreats";

// Function to render the Drawer component to display the device information
function DeviceDrawer({ deviceDetails, drawerStatus }) {
  // By default, useState is false as no network nodes has been selected on first load
  const [visible, setVisible] = useState(drawerStatus);
  // Check if drawerStatus has been updated
  // When user clicks on a node, drawerStatus will be passed to this component as "true"
  // When drawerStatus is "true", run setVisible(true) to open the drawer component
  useEffect(() => {
    if (drawerStatus == true) {
      setVisible(true);
    } else if (drawerStatus == false) {
      setVisible(false);
    }
  });
  console.log("Value passed:", drawerStatus);
  return (
    <div>
      <Drawer
        title={
          <div>
            Home / <b>Device Statistics</b>
          </div>
        }
        style={{ color: "white" }}
        maskStyle={{
          color: "white",
        }}
        headerStyle={{
          // backgroundColor: "#16151B",
          backgroundColor: "white",
          borderBottomWidth: 0,
        }}
        bodyStyle={{
          backgroundColor: "#16151B",
          color: "white",
        }}
        width={"650px"}
        placement="right"
        closable={true}
        mask={false}
        keyboard={true}
        visible={visible}
        onClose={() => {
          setVisible(false);
          drawerStatus = false;
        }}
      >
        <div>
          <Row gutter={25}>
            <Col span={12}>
              <DeviceCard deviceDetails={deviceDetails} />
            </Col>
            <Col span={12}>
              <EmergingThreatsCard />
            </Col>
          </Row>
        </div>
        <br />
        <StatisticsBoxes />
        <br />
        <ThreatsTable />
      </Drawer>
    </div>
  );
}

export default DeviceDrawer;

