import React, { useState } from "react";
import "antd/dist/antd.css";
import { Drawer, Button, Col, Row } from "antd";
import DeviceCard from "./Card_Device";
import EmergingThreatsCard from "./CardEmergingThreats";
import StatisticsBoxes from "./Statistics";
import ThreatsTable from "./TableThreats";

function DeviceDrawer({ deviceDetails, drawerStatus }) {
  // Function
  function setDrawerStatus({ drawerStatus }) {
    return setVisible(drawerStatus);
  }

  const [visible, setVisible] = useState(setDrawerStatus(drawerStatus));
  console.log("Value passed:", drawerStatus);
  return (
    <div>
      <Drawer
        title={
          <div>
            Home / <b>Device Statistics</b>
          </div>
        }
        style={{ color: "white" }}
        maskStyle={{
          color: "white",
        }}
        headerStyle={{
          // backgroundColor: "#16151B",
          backgroundColor: "white",
          borderBottomWidth: 0,
        }}
        bodyStyle={{
          backgroundColor: "#16151B",
          color: "white",
        }}
        width={"650px"}
        placement="right"
        closable={true}
        mask={false}
        keyboard={true}
        visible={visible}
        onClose={() => {
          setVisible(false);
        }}
      >
        <div>
          <Row gutter={25}>
            <Col span={12}>
              <DeviceCard deviceDetails={deviceDetails} />
            </Col>
            <Col span={12}>
              <EmergingThreatsCard />
            </Col>
          </Row>
        </div>
        <br />
        <StatisticsBoxes />
        <br />
        <ThreatsTable />
      </Drawer>
    </div>
  );
}

export default DeviceDrawer;

