import "antd/dist/antd.css";
import React, { useState, useRef, useCallback } from "react";
import { Layout, Button, Affix } from "antd";
import { ForceGraph3D } from "react-force-graph";
import DeviceDrawer from "./Components/DrawerDevice";
import NetworkDrawer from "./Components/DrawerNetwork";
import * as THREE from "three";
import networkData from "./Components/Data/network.json";
import device_data from "./Components/Data/device_data.json";
import relation_data from "./Components/Data/relation_data.json";

function AppNetworkTopology() {
  const [deviceDrawerStatus, setDeviceDrawerStatus] = useState(false);
  const [networkDrawerStatus, setNetworkDrawerStatus] = useState(false); // networkDrawerStatus={this.state.networkDrawerStatus}
  const [deviceDetails, setDeviceDetails] = useState({});
  const [networkDetails, setNetworkDetails] = useState({});
  const [groupByDevice, setGroupByDevice] = useState();
  const [deviceCounter, setDeviceCounter] = useState();

  const closeDeviceDrawer = () => {
    setDeviceDrawerStatus(false);
  };

  const closeNetworkDrawer = () => {
    setNetworkDrawerStatus(false);
  };

  const handleNetworkStatisticsButton = () => {
    setDeviceDrawerStatus(false);
    setNetworkDrawerStatus(true);
    setNetworkDetails(networkData[0]);
    setDeviceCounter(device_data.length);
    setGroupByDevice(groupByDeviceType());
  };

  // const groupByDeviceType = device_data.reduce((thisData, dev) => {
  //   thisData[dev.status] = thisData[dev.status] + 1 || 1;
  //   return thisData;
  // }, {});

  const groupByDeviceType = () => {
    var result = {};
    for (var i = 0; i < device_data.length; i++) {
      if (!result[device_data[i]["status"]]) {
        result[device_data[i]["status"]] = 0;
      }
      result[device_data[i]["status"]]++;
    }
    return result;
  };

  const fgRef = useRef();
  const zoomInNode = useCallback(
    (node) => {
      setDeviceDrawerStatus(true);
      setNetworkDrawerStatus(false);
      setDeviceDetails(node);

      // Camera distance of node
      const distance = 150;
      const distRatio = 1 + distance / Math.hypot(node.x, node.y, node.z);

      fgRef.current.cameraPosition(
        {
          x: node.x * distRatio,
          y: node.y * distRatio,
          z: node.z * distRatio,
        }, // new position
        node, // lookAt ({ x, y, z })
        2000 // ms transition duration
      );
    },
    [fgRef]
  );

  const handleNodeHover = (node) => {
    if (node) {
      console.log(node);
    } else {
      console.log("Nothing");
    }
    // highlightNodes.clear();
    // highlightLinks.clear();
    // if (node) {
    //   highlightNodes.add(node);
    //   node.neighbors.forEach((neighbor) => highlightNodes.add(neighbor));
    //   node.links.forEach((link) => highlightLinks.add(link));
    // }
  };

  // const [highlightNodes, setHighlightNodes] = useState(new Set());
  // const [highlightLinks, setHighlightLinks] = useState(new Set());
  // const [hoverNode, setHoverNode] = useState(null);

  // const updateHighlight = () => {
  //   setHighlightNodes(highlightNodes);
  //   setHighlightLinks(highlightLinks);
  // };

  // const handleNodeHover = (node) => {
  //   highlightNodes.clear();
  //   highlightLinks.clear();
  //   if (node) {
  //     highlightNodes.add(node);
  //     node.neighbors.forEach((neighbor) => highlightNodes.add(neighbor));
  //     node.links.forEach((link) => highlightLinks.add(link));
  //   }

  //   setHoverNode(node || null);
  //   updateHighlight();
  // };

  // const handleLinkHover = (link) => {
  //   highlightNodes.clear();
  //   highlightLinks.clear();

  //   if (link) {
  //     highlightLinks.add(link);
  //     highlightNodes.add(link.source);
  //     highlightNodes.add(link.target);
  //   }

  //   updateHighlight();
  // };

  const { useState, useMemo, useCallback } = React;

  const ExpandableGraph = ({ graphData }) => {
    const rootId = 0;

    const nodesById = useMemo(() => {
      const nodesById = Object.fromEntries(
        graphData.nodes.map((node) => [node.id, node])
      );

      // link parent/children
      graphData.nodes.forEach((node) => {
        node.collapsed = node.id !== rootId;
        node.childLinks = [];
      });
      graphData.links.forEach((link) =>
        nodesById[link.source].childLinks.push(link)
      );

      return nodesById;
    }, [graphData]);

    const getPrunedTree = useCallback(() => {
      const visibleNodes = [];
      const visibleLinks = [];
      (function traverseTree(node = nodesById[rootId]) {
        visibleNodes.push(node);
        if (node.collapsed) return;
        visibleLinks.push(...node.childLinks);
        node.childLinks
          .map((link) =>
            typeof link.target === "object"
              ? link.target
              : nodesById[link.target]
          ) // get child node
          .forEach(traverseTree);
      })();

      return { nodes: visibleNodes, links: visibleLinks };
    }, [nodesById]);

    const [prunedTree, setPrunedTree] = useState(getPrunedTree());

    const handleNodeClick = useCallback((node) => {
      node.collapsed = !node.collapsed; // toggle collapse state
      setPrunedTree(getPrunedTree());
    }, []);

    return (
      <ForceGraph3D
        graphData={prunedTree}
        linkDirectionalParticles={2}
        nodeColor={(node) =>
          !node.childLinks.length ? "green" : node.collapsed ? "red" : "yellow"
        }
        onNodeClick={handleNodeClick}
      />
    );
  };

  return (
    // <div style={{ background: "black" }}>
    //   <Layout style={{ marginLeft: 199, zIndex: -1 }}>
    //     <ForceGraph3D
    //       width={window.innerWidth - 199}
    //       height={window.innerHeight - 1}
    //       ref={fgRef}
    //       graphData={{
    //         nodes: device_data,
    //         links: relation_data,
    //       }}
    //       nodeLabel={(node) =>
    //         `<div style="background: #1F1E26; padding: 5px 10px 8px 10px; border-radius: 10px; user-select: none">
    //         <span>Name: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>${node.name}</b></span><br/>
    //         <span>IP Addr: &nbsp;&nbsp;&nbsp;&nbsp;<b>${node.id}</b></span><br/>
    //         <span>Mac Addr: &nbsp;<b>${node.mac_addr}</b></span>
    //         </div>`
    //       }
    //       linkWidth={2}
    //       onNodeRightClick={zoomInNode}
    //       linkDirectionalParticles={1}
    //       linkDirectionalParticleSpeed={0.01}
    //       onNodeHover={handleNodeHover}
    //       // onNodeHover={handleHover}
    //       // onNodeDragEnd={(node) => {
    //       //   node.fx = node.x;
    //       //   node.fy = node.y;
    //       //   node.fz = node.z;
    //       // }}
    //       nodeThreeObject={({ type, status }) => {
    //         let imgTexture = null;
    //         if (type === "DB") {
    //           if (status === "Active") {
    //             imgTexture = new THREE.TextureLoader().load(
    //               "assets/online_db.png"
    //             );
    //           } else if (status === "Inactive") {
    //             imgTexture = new THREE.TextureLoader().load(
    //               "assets/inactive_db.png"
    //             );
    //           } else {
    //             imgTexture = new THREE.TextureLoader().load(
    //               "assets/offline_db.png"
    //             );
    //           }
    //         } else if (type === "PC") {
    //           if (status === "Active") {
    //             imgTexture = new THREE.TextureLoader().load(
    //               "assets/online_pc.png"
    //             );
    //           } else if (status === "Inactive") {
    //             imgTexture = new THREE.TextureLoader().load(
    //               "assets/inactive_pc.png"
    //             );
    //           } else {
    //             imgTexture = new THREE.TextureLoader().load(
    //               "assets/offline_pc.png"
    //             );
    //           }
    //         } else if (type === "Router") {
    //           if (status === "Active") {
    //             imgTexture = new THREE.TextureLoader().load(
