import "antd/dist/antd.css";
import React, { useState, useRef, useCallback, useEffect } from "react";
import { Layout, Button, Affix } from "antd";
import { ForceGraph3D } from "react-force-graph";
import DeviceDrawer from "./Components/DrawerDevice";
import NetworkDrawer from "./Components/DrawerNetwork";
import * as THREE from "three";
import networkData from "./Components/Data/network.json";
import device_data from "./Components/Data/device_data.json";
import relation_data from "./Components/Data/relation_data.json";
import { useMemo } from "react/cjs/react.production.min";

// const ForceGraphComponent = React.memo((props) => {console.log("Child rendered"); // For verification purposes

function AppNetworkTopology() {
  const [deviceDrawerStatus, setDeviceDrawerStatus] = useState(false);
  const [networkDrawerStatus, setNetworkDrawerStatus] = useState(false); // networkDrawerStatus={this.state.networkDrawerStatus}
  const [deviceDetails, setDeviceDetails] = useState({});
  const [networkDetails, setNetworkDetails] = useState({});
  const [groupByDevice, setGroupByDevice] = useState();
  const [deviceCounter, setDeviceCounter] = useState();
  const [hoverNode, setHoverNode] = useState();
  const [deviceConnected, setDeviceConnected] = useState();

  const [alertData, setAlertData] = useState([]);

  // getData() with fetch(), run GET to API to get latest data from JSON Server
  // Store latest data to alertData
  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    await fetch("http://localhost:3001/alerts")
      .then((response) => response.json())
      .then((data) => setAlertData(data))
      .catch((err) => console.log(err));
  };

  const this3DGraph = useMemo(() => {
    return (
      <ForceGraph3D
        width={window.innerWidth}
        height={window.innerHeight - 1}
        ref={fgRef}
        graphData={{
          nodes: device_data,
          links: relation_data,
        }}
        nodeLabel={(node) =>
          `<div style="background: #1F1E26; padding: 5px 10px 8px 10px; border-radius: 10px; user-select: none">
            <span>Name: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>${node.name}</b></span><br/>
            <span>IP Addr: &nbsp;&nbsp;&nbsp;&nbsp;<b>${node.id}</b></span><br/>
            <span>Mac Addr: &nbsp;<b>${node.mac_addr}</b></span>
            </div>`
        }
        linkWidth={1}
        onNodeRightClick={zoomInNode}
        onNodeHover={handleNodeHover}
        // linkDirectionalParticles={1}
        // linkDirectionalParticleSpeed={0.01}
        // onNodeHover={handleHover}
        // onNodeDragEnd={(node) => {
        //   node.fx = node.x;
        //   node.fy = node.y;
        //   node.fz = node.z;
        // }}
        nodeThreeObject={updateNodeIcons}
      />
    );
  });

  const closeDeviceDrawer = () => {
    setDeviceDrawerStatus(false);
  };

  const closeNetworkDrawer = () => {
    setNetworkDrawerStatus(false);
  };

  const handleNetworkStatisticsButton = () => {
    setDeviceDrawerStatus(false);
    setNetworkDrawerStatus(true);
    setNetworkDetails(networkData[0]);
    setDeviceCounter(device_data.length);
    setGroupByDevice(groupByDeviceType());
  };

  // const groupByDeviceType = device_data.reduce((thisData, dev) => {
  //   thisData[dev.status] = thisData[dev.status] + 1 || 1;
  //   return thisData;
  // }, {});

  const groupByDeviceType = () => {
    var result = {};
    for (var i = 0; i < device_data.length; i++) {
      if (!result[device_data[i]["status"]]) {
        result[device_data[i]["status"]] = 0;
      }
      result[device_data[i]["status"]]++;
    }
    return result;
  };

  const fgRef = useRef();
  const zoomInNode = useCallback(
    (node) => {
      setDeviceDrawerStatus(true);
      setNetworkDrawerStatus(false);
      setDeviceDetails(node);
      setHoverNode(node);

      // Camera distance of node
      const distance = 150;
      const distRatio = 1 + distance / Math.hypot(node.x, node.y, node.z);

      fgRef.current.cameraPosition(
        {
          x: node.x * distRatio,
          y: node.y * distRatio,
          z: node.z * distRatio,
        }, // new position
        node, // lookAt ({ x, y, z })
        3000 // ms transition duration
      );
    },
    [fgRef]
  );

  var this_array = [];

  const handleNodeHover = (node) => {
    // this_array = [];
    // if (node) {
    //   console.log(node);
    //   for (var i = 0; i < relation_data.length; i++) {
    //     if (relation_data[i].source.id === node.id) {
    //       this_array.push(relation_data[i].target.id);
    //     } else if (relation_data[i].target.id === node.id) {
    //       this_array.push(relation_data[i].source.id);
    //     }
    //   }
    //   console.log(this_array);
    // } else {
    //   // console.log("Nothing");
    // }
  };

  const updateNodeIcons = useCallback((node) => {
    let imgTexture = null;
    this_array = [];

    if (hoverNode) {
      for (var i = 0; i < relation_data.length; i++) {
        if (relation_data[i].source.id === hoverNode.id) {
          this_array.push(relation_data[i].target.id);
        } else if (relation_data[i].target.id === hoverNode.id) {
          this_array.push(relation_data[i].source.id);
        }
      }
    }

    setDeviceConnected(this_array);

    if (node === hoverNode) {
      console.log("Hello", node);

      console.log("haha", relation_data[0]);

      for (var i = 0; i < relation_data.length; i++) {
        if (relation_data[i].source.id === node.id) {
          this_array.push(relation_data[i].target.id);
        } else if (relation_data[i].target.id === node.id) {
          this_array.push(relation_data[i].source.id);
        }
      }
      console.log("Connected to", this_array);
      console.log("============================================");

      // imgTexture = new THREE.TextureLoader().load("assets/check.png");

      if (node.type === "DB") {
        if (node.status === "Active") {
          imgTexture = new THREE.TextureLoader().load(
            "assets/online_db_selected.png"
          );
        } else if (node.status === "Inactive") {
          imgTexture = new THREE.TextureLoader().load(
            "assets/inactive_db_selected.png"
          );
        } else {
          imgTexture = new THREE.TextureLoader().load(
            "assets/offline_db_selected.png"
          );
        }
      } else if (node.type === "PC") {
        if (node.status === "Active") {
          imgTexture = new THREE.TextureLoader().load(
            "assets/online_pc_selected.png"
          );
        } else if (node.status === "Inactive") {
          imgTexture = new THREE.TextureLoader().load(
            "assets/inactive_pc_selected.png"
          );
        } else {
          imgTexture = new THREE.TextureLoader().load(
            "assets/offline_pc_selected.png"
          );
        }
      } else if (node.type === "Router") {
        if (node.status === "Active") {
          imgTexture = new THREE.TextureLoader().load(
            "assets/online_router_selected.png"
          );
        } else if (node.status === "Inactive") {
          imgTexture = new THREE.TextureLoader().load(
            "assets/inactive_router_selected.png"
          );
        } else {
          imgTexture = new THREE.TextureLoader().load(
            "assets/offline_router_selected.png"
          );
        }
      }

      // For nodes that are not selected
    } else {
      if (node.type === "DB") {
        if (node.status === "Active") {
          imgTexture = new THREE.TextureLoader().load("assets/online_db.png");
        } else if (node.status === "Inactive") {
          imgTexture = new THREE.TextureLoader().load("assets/inactive_db.png");
        } else {
          imgTexture = new THREE.TextureLoader().load("assets/offline_db.png");
        }
      } else if (node.type === "PC") {
        if (node.status === "Active") {
          imgTexture = new THREE.TextureLoader().load("assets/online_pc.png");
        } else if (node.status === "Inactive") {
          imgTexture = new THREE.TextureLoader().load("assets/inactive_pc.png");
        } else {
          imgTexture = new THREE.TextureLoader().load("assets/offline_pc.png");
        }
      } else if (node.type === "Router") {
        if (node.status === "Active") {
          imgTexture = new THREE.TextureLoader().load(
            "assets/online_router.png"
          );
        } else if (node.status === "Inactive") {
          imgTexture = new THREE.TextureLoader().load(
            "assets/inactive_router.png"
          );
        } else {
          imgTexture = new THREE.TextureLoader().load(
            "assets/offline_router.png"
          );
        }
      }
    }

    const material = new THREE.SpriteMaterial({ map: imgTexture });
    const sprite = new THREE.Sprite(material);
    sprite.scale.set(12, 12);

    return sprite;
  });

  // <Layout style={{ marginLeft: 199, zIndex: -1 }}>

  return (
    <div>
      <Layout>
        <this3DGraph />
      </Layout>
      <Affix style={{ position: "absolute", top: 120, left: 10, zIndex: 1 }}>
        <Button type="primary" onClick={handleNetworkStatisticsButton}>
          Show Network Statistics
        </Button>
      </Affix>

      <DeviceDrawer
        deviceDrawerStatus={deviceDrawerStatus}
        deviceDetails={deviceDetails}
        closeDeviceDrawer={closeDeviceDrawer}
        alertData={alertData}
        deviceConnected={deviceConnected}
      />
      <NetworkDrawer
        networkDrawerStatus={networkDrawerStatus}
        networkDetails={networkDetails}
        groupByDevice={groupByDevice}
        deviceCounter={deviceCounter}
        closeNetworkDrawer={closeNetworkDrawer}
        alertData={alertData}
      />
    </div>
  );
}

