import "antd/dist/antd.css";
import React from "react";
import { Button } from "antd";
import { ForceGraph3D } from "react-force-graph";
import DeviceDrawer from "./Components/DrawerDevice";
import * as THREE from "three";
// import testingData from "./Components/Data/testdata.json";

export default class AppNetworkTopology extends React.Component {
  // Initial state for deviceDetailsObject & drawerStatus
  state = {
    deviceDetailsObject: {},
    drawerStatus: false,
  };

  // On right-click on icon on network topology, pass the object to handleRightClick()
  // Set deviceDetailsObject as node
  handleRightClick = (node) => {
    this.setState({ deviceDetailsObject: node, drawerStatus: true });
  };

  closeDrawer = () => {
    this.setState({ drawerStatus: false });
  };

  render() {
    return (
      <div
        style={{
          background: "black",
          width: 700,
          padding: 30,
        }}
      >
        <Button type="primary" onClick={() => {}}>
          Open Drawer
        </Button>
        <DeviceDrawer
          deviceDetails={this.state.deviceDetailsObject}
          drawerStatus={this.state.drawerStatus}
          closeDrawerFunction={this.closeDrawer}
        />
        <ForceGraph3D
          graphData={{
            nodes: [
              {
                id: "192.168.1.100",
                name: "DB-01",
                type: "DB",
                value: "1",
                os: "Linux",
                mac_addr: "aa:aa:aa:aa:aa:aa",
                subnet: "255.255.255.0",
                gateway: "192.168.1.1",
              },
              {
                id: "192.168.1.101",
                name: "PC-01",
                type: "PC",
                value: "1",
                os: "Windows 10",
                mac_addr: "bb:bb:bb:bb:bb:bb",
                subnet: "255.255.255.0",
                gateway: "192.168.1.1",
              },
              {
                id: "192.168.1.102",
                name: "PC-02",
                type: "PC",
                value: "1",
                os: "Windows 10",
                mac_addr: "cc:cc:cc:cc:cc:cc",
                subnet: "255.255.255.0",
                gateway: "192.168.1.1",
              },
            ],
            links: [
              {
                source: "192.168.1.100",
                target: "192.168.1.101",
              },
              {
                source: "192.168.1.101",
                target: "192.168.1.102",
              },
              {
                source: "192.168.1.100",
                target: "192.168.1.102",
              },
            ],
          }}
          onNodeRightClick={this.handleRightClick}
          linkDirectionalParticles={1}
          linkDirectionalParticleSpeed={0.01}
          onNodeDragEnd={(node) => {
            node.fx = node.x;
            node.fy = node.y;
            node.fz = node.z;
          }}
          nodeThreeObject={({ type }) => {
            let imgTexture = null;
            if (type === "DB") {
              imgTexture = new THREE.TextureLoader().load("assets/img_db.png");
            } else if (type === "PC") {
              imgTexture = new THREE.TextureLoader().load("assets/img_pc.png");
            }

            const material = new THREE.SpriteMaterial({ map: imgTexture });
            const sprite = new THREE.Sprite(material);
            sprite.scale.set(12, 12);

            return sprite;
          }}
        />
      </div>
    );
  }
}

