  const zoomInNode = useCallback(
    (node) => {
      setDeviceDrawerStatus(true);
      setNetworkDrawerStatus(false);
      setDeviceDetails(node);

      // Aim at node from outside it
      const distance = 120;
      const distRatio = 1 + distance / Math.hypot(node.x, node.y, node.z);

      fgRef.current.cameraPosition(
        { x: node.x * distRatio, y: node.y * distRatio, z: node.z * distRatio }, // new position
        node, // lookAt ({ x, y, z })
        3000 // ms transition duration
      );
    },
    [fgRef]
  );

  const ExpandableGraph = ({ graphData }) => {
    const rootId = 0;

    const nodesById = useMemo(() => {
      const nodesById = Object.fromEntries(
        graphData.nodes.map((node) => [node.id, node])
      );

      // link parent/children
      graphData.nodes.forEach((node) => {
        node.collapsed = node.id !== rootId;
        node.childLinks = [];
      });
      graphData.links.forEach((link) =>
        nodesById[link.source].childLinks.push(link)
      );

      return nodesById;
    }, [graphData]);

    const getPrunedTree = useCallback(() => {
      const visibleNodes = [];
      const visibleLinks = [];
      (function traverseTree(node = nodesById[rootId]) {
        visibleNodes.push(node);
        if (node.collapsed) return;
        visibleLinks.push(...node.childLinks);
        node.childLinks
          .map((link) =>
            typeof link.target === "object"
              ? link.target
              : nodesById[link.target]
          ) // get child node
          .forEach(traverseTree);
      })();

      return { nodes: visibleNodes, links: visibleLinks };
    }, [nodesById]);

    const [prunedTree, setPrunedTree] = useState(getPrunedTree());

    const handleNodeClick = useCallback((node) => {
      node.collapsed = !node.collapsed; // toggle collapse state
      setPrunedTree(getPrunedTree());
    }, []);

    return (
      <ForceGraph3D
        graphData={prunedTree}
        linkDirectionalParticles={2}
        nodeColor={(node) =>
          !node.childLinks.length ? "green" : node.collapsed ? "red" : "yellow"
        }
        onNodeClick={handleNodeClick}
      />
    );
  };

  const NODE_R = 8;
  const HighlightGraph = () => {
    const data = useMemo(() => {
      const gData = genRandomTree(80);

      // cross-link node objects
      gData.links.forEach((link) => {
        const a = gData.nodes[link.source];
        const b = gData.nodes[link.target];
        !a.neighbors && (a.neighbors = []);
        !b.neighbors && (b.neighbors = []);
        a.neighbors.push(b);
        b.neighbors.push(a);

        !a.links && (a.links = []);
        !b.links && (b.links = []);
        a.links.push(link);
        b.links.push(link);
      });

      return gData;
    }, []);

    const [highlightNodes, setHighlightNodes] = useState(new Set());
    const [highlightLinks, setHighlightLinks] = useState(new Set());
    const [hoverNode, setHoverNode] = useState(null);

    const updateHighlight = () => {
      setHighlightNodes(highlightNodes);
      setHighlightLinks(highlightLinks);
    };

    const handleNodeHover = (node) => {
      highlightNodes.clear();
      highlightLinks.clear();
      if (node) {
        highlightNodes.add(node);
        node.neighbors.forEach((neighbor) => highlightNodes.add(neighbor));
        node.links.forEach((link) => highlightLinks.add(link));
      }

      setHoverNode(node || null);
      updateHighlight();
    };

    const handleLinkHover = (link) => {
      highlightNodes.clear();
      highlightLinks.clear();

      if (link) {
        highlightLinks.add(link);
        highlightNodes.add(link.source);
        highlightNodes.add(link.target);
      }

      updateHighlight();
    };

    const paintRing = useCallback(
      (node, ctx) => {
        // add ring just for highlighted nodes
        ctx.beginPath();
        ctx.arc(node.x, node.y, NODE_R * 1.4, 0, 2 * Math.PI, false);
        ctx.fillStyle = node === hoverNode ? "red" : "orange";
        ctx.fill();
      },
      [hoverNode]
    );

    return (
      <ForceGraph2D
        graphData={data}
        nodeRelSize={NODE_R}
        autoPauseRedraw={false}
        linkWidth={(link) => (highlightLinks.has(link) ? 5 : 1)}
        linkDirectionalParticles={4}
        linkDirectionalParticleWidth={(link) =>
          highlightLinks.has(link) ? 4 : 0
        }
        nodeCanvasObjectMode={(node) =>
          highlightNodes.has(node) ? "before" : undefined
        }
        nodeCanvasObject={paintRing}
        onNodeHover={handleNodeHover}
        onLinkHover={handleLinkHover}
      />
    );
  };

  return (
    <div>
      <Layout style={{ marginLeft: 199 }}>
        {" "}
        <ForceGraph3D
          style={{ zIndex: -1 }}
          width={window.innerWidth - 200}
          ref={fgRef}
          graphData={{
            nodes: device_data,
            links: relation_data,
          }}
          onNodeRightClick={zoomInNode}
          // linkDirectionalParticles={1}
          // linkDirectionalParticleSpeed={0.01}
          // onNodeHover={handleHover}
          // onNodeDragEnd={(node) => {
          //   node.fx = node.x;
          //   node.fy = node.y;
          //   node.fz = node.z;
          // }}
          nodeThreeObject={({ type }) => {
            let imgTexture = null;
            if (type === "DB") {
              imgTexture = new THREE.TextureLoader().load("assets/img_db.png");
            } else if (type === "PC") {
              imgTexture = new THREE.TextureLoader().load("assets/img_pc.png");
            } else if (type === "Router") {
              imgTexture = new THREE.TextureLoader().load(
                "assets/img_router.png"
              );
            }

            const material = new THREE.SpriteMaterial({ map: imgTexture });
            const sprite = new THREE.Sprite(material);
            sprite.scale.set(12, 12);

            return sprite;
          }}
        />
      </Layout>
      <Affix style={{ position: "absolute", top: 120, left: 10 }}>
        <Button type="primary" onClick={handleNetworkStatisticsButton}>
          Show Network Statistics
        </Button>
      </Affix>

      <DeviceDrawer
        deviceDrawerStatus={deviceDrawerStatus}
        deviceDetails={deviceDetails}
        closeDeviceDrawer={closeDeviceDrawer}
      />
      <NetworkDrawer
        networkDrawerStatus={networkDrawerStatus} // networkDrawerStatus={this.state.networkDrawerStatus}
        networkDetails={networkDetails} // networkDetailObject={this.state.networkDetailObject}
        groupByDevice={groupByDevice} // networkStatisticsObject={this.state.networkStatisticsObject}
        deviceCounter={deviceCounter} // networkDeviceCounter={this.state.networkDeviceCounter}
        closeNetworkDrawer={closeNetworkDrawer} // closeNetworkDrawerFunction={this.closeNetworkDrawer}
      />
    </div>
  );
}

export default AppNetworkTopology;
