import { Table } from "antd";
import alerts from "./Data/alert.json";

function ThreatsTable() {
  var alert_data = [];

  fetch("http://localhost:3001/alerts").then((data) => (alert_data = data));

  return (
    <div>
      <h3 style={{ color: "white" }}>Detected Cyber Security Threats</h3>
      <Table
        dataSource={alert_data}
        columns={columns}
        pagination={{
          defaultPageSize: 5,
        }}
      />
    </div>
  );
}

const dataSource = [];

const columns = [
  {
    title: "Timestamp",
    dataIndex: "timestamp",
    key: "timestamp",
    sorter: (a, b) => a.timestamp - b.timestamp,
  },
  {
    title: "Source",
    dataIndex: "source",
    key: "source",
  },
  {
    title: "Destination",
    dataIndex: "destination",
    key: "destination",
  },
  {
    title: "Threat Info",
    dataIndex: "threatinfo",
    render: (text, record) => (
      <span>
        <b>Threat Category: </b>
        {record.threat_category} <br />
        <b>Threat Name: </b>
        {record.threat_name} <br />
      </span>
    ),
    key: "threat",
    filters: [
      {
        text: "Policy Violation",
        value: "Policy Violation",
      },
      {
        text: "Suspicious",
        value: "Suspicious",
      },
    ],
    onFilter: (value, record) => record.threat_category.includes(value),
    filterMode: "tree",
    filterSearch: true,
  },
  // {
  //   title: "Threat Type",
  //   dataIndex: "threat",
  //   key: "threat",
  //   filters: [
  //     {
  //       text: "Trojan",
  //       value: "Trojan",
  //     },
  //     {
  //       text: "Rootkit",
  //       value: "Rootkit",
  //     },
  //   ],
  //   onFilter: (value, record) => record.threat.includes(value),
  //   filterMode: "tree",
  //   filterSearch: true,
  // },
];

export default ThreatsTable;
