import "antd/dist/antd.css";
import { ForceGraph3D } from "react-force-graph";
import React from "react";
import * as THREE from "three";

function AppNetworkTopology() {
  const handleClick = (node) => {
    alert(node.id);
  };

  // const imgs = ["hosting.png"];
  const highlightNodes = new Set();
  const highlightLinks = new Set();
  let hoverNode = null;

  return (
    <div>
      <ForceGraph3D
        graphData={{
          nodes: [
            {
              id: "192.168.1.100",
              name: "DB-01",
              type: "DB",
              value: "10",
            },
            {
              id: "192.168.1.101",
              name: "PC-01",
              type: "PC",
              value: "1",
            },
            {
              id: "192.168.1.102",
              name: "PC-02",
              type: "PC",
              value: "1",
            },
          ],
          links: [
            {
              source: "192.168.1.100",
              target: "192.168.1.101",
            },
            {
              source: "192.168.1.101",
              target: "192.168.1.102",
            },
            {
              source: "192.168.1.100",
              target: "192.168.1.102",
            },
          ],
        }}
        onNodeHover={(node) => {
          highlightNodes.clear();
          highlightLinks.clear();
          if (node) {
            highlightNodes.add(node);
            node.neighbors.forEach((neighbor) => highlightNodes.add(neighbor));
            node.links.forEach((link) => highlightLinks.add(link));
          }

          hoverNode = node || null;
        }}
        onNodeRightClick={handleClick}
        linkDirectionalParticles={1}
        linkDirectionalParticleSpeed={0.01}
        nodeThreeObject={({ type }) => {
          let imgTexture = null;
          if (type === "DB") {
            imgTexture = new THREE.TextureLoader().load(
              `https://cdn-icons.flaticon.com/png/512/1043/premium/1043315.png?token=exp=1639038595~hmac=a9ae8e0051e2519aab494b49369ba6b8`
            );
          } else {
            imgTexture = new THREE.TextureLoader().load(
              `https://cdn-icons.flaticon.com/png/512/1043/premium/1043315.png?token=exp=1639038595~hmac=a9ae8e0051e2519aab494b49369ba6b8`
            );
          }

          const material = new THREE.SpriteMaterial({ map: imgTexture });
          const sprite = new THREE.Sprite(material);
          sprite.scale.set(12, 12);

          return sprite;
        }}
      />
    </div>
  );
}

function genRandomTree(N = 300, reverse = false) {
  return {
    nodes: [...Array(N).keys()].map((i) => ({ id: i })),
    links: [...Array(N).keys()]
      .filter((id) => id)
      .map((id) => ({
        [reverse ? "target" : "source"]: id,
        [reverse ? "source" : "target"]: Math.round(Math.random() * (id - 1)),
      })),
  };
}

export default AppNetworkTopology;
