import React, { useState } from "react";
import "antd/dist/antd.css";
import { Drawer, Button, Col, Row } from "antd";
import DeviceCard from "./Card_Device";
import EmergingThreatsCard from "./CardEmergingThreats";
import StatisticsBoxes from "./Statistics";
import ThreatsTable from "./TableThreats";

class DeviceDrawer extends React.Component {
  state = {
    deviceDetailsObject: {},
  };
  render() {
    return (
      <div
        style={{
          display: "block",
          width: 700,
          padding: 30,
        }}
      >
        <h4>Test Drawer</h4>
        <p>{deviceDetails.os}</p>
        <Button
          type="primary"
          onClick={() => {
            setVisible(true);
          }}
        >
          Open Drawer
        </Button>
        <br />
        <br />
        <Button
          type="primary"
          onClick={() => {
            setVisible(false);
          }}
        >
          Close Drawer
        </Button>
        <Drawer
          title={
            <div>
              Home / <b>Device Statistics</b>
            </div>
          }
          style={{ color: "white" }}
          maskStyle={{
            color: "white",
          }}
          headerStyle={{
            // backgroundColor: "#16151B",
            backgroundColor: "white",
            borderBottomWidth: 0,
          }}
          bodyStyle={{
            backgroundColor: "#16151B",
            color: "white",
          }}
          width={"650px"}
          placement="right"
          closable={true}
          mask={false}
          keyboard={true}
          visible={visible}
          onClose={() => {
            setVisible(false);
          }}
        >
          <div>
            <Row gutter={25}>
              <Col span={12}>
                <DeviceCard deviceDetails={deviceDetails} />
              </Col>
              <Col span={12}>
                <EmergingThreatsCard />
              </Col>
            </Row>
          </div>
          <br />
          <StatisticsBoxes />
          <br />
          <ThreatsTable />
        </Drawer>
      </div>
    );
  }
}

export default DeviceDrawer;

